def calculate_answer(prompt):
    tokens = prompt.split()

    if len(tokens) == 0:
        return b""

    first_token = int(tokens[0], 2)
    result = first_token

    for token in tokens[1:]:
        try:
            op = int(token)
            result = calculate_operation(result, op)
        except ValueError:
            raise Exception("Invalid token.")

    return result

def calculate_operation(a, op):
    if op == 0:
        return a
    elif op == 1:
        return a + 1
    else:
        raise Exception("Unknown operation")
