def majority_element(arr):
    majority_count = len(arr)//2
    majority_index = 0
    for i in range(majority_count):
        if (sum(1 for j in range(i*majority_count, (i+1)*majority_count) if arr[j] == arr[j+majority_count]) > (majority_count-1)/2):
            majority_index += majority_count
        else:
            majority_index += i*majority_count
    return arr[majority_index]

