import os
from collections import Counter

def find_top_words(directory):
    word_counts = {}
    for filename in os.listdir(directory):
        if filename.endswith('.txt'):
            with open(os.path.join(directory, filename), 'r') as file:
                text = file.read()
                words = text.split()
                for word in words:
                    word = word.lower()
                    word_counts[word] = word_counts.get(word, 0) + 1

    sorted_words = sorted(word_counts.items(), key=lambda x: x[1], reverse=True)
    return [word for word, count in sorted_words[:5]]

import multiprocessing

def find_top_words(directory):
    word_counts = {}
    with multiprocessing.Pool() as pool:
        for filename in os.listdir(directory):
            if filename.endswith('.txt'):
                pool.apply_async(lambda: (os.path.join(directory, filename), pool.imap_unordered(lambda: text.split(), open(os.path.join(directory, filename), 'r').readlines())))
        for filename, lines in pool.imap_result():
            for word in lines:
                word = word.lower()
                word_counts[word] = word_counts.get(word, 0) + sum(1 for _ in lines)

    sorted_words = sorted(word_counts.items(), key=lambda x: x[1], reverse=True)
    return [word for word, count in sorted_words[:5]]

