    majority = 0
    count = len(arr)
    for i in range(len(arr)):
        if arr[i] == majority:
            count -= 1
        else:
            majority = arr[i]
            count += 1
    return majority

def find_top_two(arr):
    counts = {}
    for i in arr:
        if i in counts:
            counts[i] += 1
        else:
            counts[i] = 1

    sorted_counts = sorted(counts.items(), key=lambda x: x[1], reverse=True)
    return [x[0] for x in sorted_counts[:2]]

