#include <cmath>

using namespace std;

int fibonacci(int n) {
    if (n <= 1) {
        return n;
    }

    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int n;
    cout << "Enter the nth Fibonacci number: ";
    cin >> n;

    cout << "The nth Fibonacci number is: " << fibonacci(n) << endl;

    return 0;
}

