def highest_common_ancestor(node1, node2):
    if node1 is None or node2 is None:
        return None

    if node1.val == node2.val:
        return node1

    left_node1 = node1.left
    right_node1 = node1.right
    left_node2 = node2.left
    right_node2 = node2.right

    while left_node1 and left_node2:
        if left_node1.val == left_node2.val:
            left_node1, left_node2 = left_node1.left, left_node2.left
        else:
            left_node1 = left_node1.left
            left_node2 = left_node2.right

    if left_node1 and right_node2:
        return left_node1
    elif left_node2 and right_node1:
        return left_node2
    else:
        return None

return highest_common_ancestor(node1, node2)

