def median_of_two_sorted_arrays(arr1, arr2):
    n1 = len(arr1)
    n2 = len(arr2)

    if n1 > n2:
        return median_of_two_sorted_arrays(arr2, arr1)

    if n1 < n2:
        return median_of_two_sorted_arrays(arr1, arr2)

    if n1 == n2:
        return arr1[n1 // 2]

    i = 0
    j = n1 - 1

    while i < n2 and j >= 0:
        if arr1[i] < arr2[j]:
            i += 1
        else:
            j -= 1

    if i == n2:
        return arr2[j]
    else:
        return arr1[i]


