def majority_element(arr):
    n = len(arr)
    majority_element = arr[0]

    for element in arr[1:]:
        majority_count = 0
        for i in range(n):
            if arr[i] == element:
                majority_count += 1

        if majority_count > n / 2:
            majority_element = element

    return majority_element


