    # If the middle element of the larger array is greater than the smaller array, then the median is in the smaller array
    if larger_arr[mid_larger] > smaller_arr:
        # Perform a binary search on the smaller array to find the median
        low, high = 0, len(smaller_arr)
        while low <= high:
            mid = (low + high) // 2
            if smaller_arr[mid] > larger_arr[mid_larger]:
                low = mid + 1
            else:
                high = mid - 1
        median = smaller_arr[high]
    # If the middle element of the larger array is less than the smaller array, then the median is in the larger array
