def majority_element(array):
    n = len(array)
    if n == 0:
        return None

    # Initialize the count of the candidate element
    candidate_count = 0
    candidate = array[0]

    # Iterate through the array and count the candidate element
    for element in array:
        if element == candidate:
            candidate_count += 1

    # Check if the candidate element is the majority element
    if candidate_count > n // 2:
        return candidate

    # If not, repeat the process with the remaining elements
    for element in array:
        if element != candidate:
            candidate_count = 0
            candidate = element
            break

    return majority_element(array)

