const radios = document.querySelectorAll('input[type="radio"]');
const checkboxes = document.querySelectorAll('input[type="checkbox"]');

// Set up event listener for all radio buttons and checkboxes
radios.forEach(radio => radio.addEventListener('change', updateCount));
checkboxes.forEach(checkbox => checkbox.addEventListener('change', updateCount));

function updateCount() {
  // Calculate total number of radio buttons and checkboxes selected
  let count = 0;
  radios.forEach(radio => {
    if (radio.checked) count++;
  });
  checkboxes.forEach(checkbox => {
    if (checkbox.checked) count++;
  });

  // Display total number of selected radio buttons and checkboxes
  let statusMessage = document.getElementById('statusMessage');
  statusMessage.innerText = `You have selected ${count} radio buttons and checkboxes`;

  // Set values of all radio buttons and checkboxes unselected
  radios.forEach(radio => {
    radio.checked = false;
    radio.value = '';
  });
  checkboxes.forEach(checkbox => {
    checkbox.checked = false;
  });
}
```
