#include <iostream>
using namespace std;
int main() {
    for(int i = 1; i <= 4; ++i) {
        cout << "Test: ";
        cout << "1 + 2 = " << i + 1 << endl;

        if((i == 3 || i == 4))
            cout << ", Test: ";
        else
            cout << endl;

        // testing for println
        System::Printf("Actor: %s,", i); 
        //testing for System.printf
        system("PAUSE");
        return 0;
    }
    return 0;
}

