const data = [
  [0, '2020-03-04', 10],
  [1, '2020-03-05', 20],
  [2, '2020-03-04', 15],
  [3, '2020-03-05', 15],
];

const maxDate = data.reduce((a, {day, value}) => Math.max(a, day === new Date().getDate? value : 0), 0);

console.log(maxDate);

