        self.stack1 = [] 
        self.stack2 = [] 

    def isEmpty(self): 
        return self.stack1 == [] and self.stack2 == [] 

    def enQueue(self, val): 
        self.stack1.append(val) 

    def deQueue1(self): 
        if not self.stack1: 
            val = self.stack2.pop() 
            if not self.stack2: 
