#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string text_file = "example.txt";
    std::string search_word = "test";

    std::ifstream file(text_file);
    std::string line;
    int word_count = 0;

    while (std::getline(file, line)) {
        size_t position = 0;
        while ((position = line.find(search_word, position))!= std::string::npos) {
            word_count++;
            position += search_word.length();
        }
    }

    std::cout << "Occurrences of '" << search_word << "': " << word_count << std::endl;

    file.close();

    return 0;
}

