def binary_search(nums, target):
  start = 0
  end = len(nums) - 1
  while start <= end:
    mid = (start + end) // 2
    if nums[mid] == target:
      return mid
    if nums[mid] < target:
      start = mid + 1
    else:
      end = mid - 1
  return -1

