class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def is_empty(self):
        return len(self.stack1) == 0 and len(self.stack2) == 0

    def enqueue(self, val):
        if not self.is_empty():
            self.stack2.append(self.stack1.pop())
        self.stack1.append(val)

    def dequeue(self):
        if not self.is_empty():
            return self.stack1.pop()
        else:
            return self.stack2.pop()

