def longest_common_subsequence(s1, s2):
    m = len(s1)
    n = len(s2)
    dp_table = [[0 for i in range(n + 1)] for j in range(m + 1)]

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if s1[i - 1] == s2[j - 1]:
                dp_table[i][j] = dp_table[i - 1][j - 1] + 1
            else:
                dp_table[i][j] = max(dp_table[i - 1][j], dp_table[i][j - 1])

    lcs_length = dp_table[m][n]
    lcs_array = [''] * lcs_length

    i = m
    j = n
    prev_char = s1[0]
    while i > 0 and j > 0:
        if s1[i - 1] == s2[j - 1]:
            lcs_array[lcs_length - 1] = s1[i - 1]
            i -= 1
            j -= 1
            lcs_length -= 1
        elif dp_table[i - 1][j] > dp_table[i][j - 1]:
            i -= 1
        else:
            j -= 1

    return ''.join(lcs_array)

