def binary_search(array, target):
  left, right = 0, len(array) - 1
  while left <= right:
    mid = (left + right) // 2
    if array[mid] < target:
      left = mid + 1
    elif array[mid] > target:
      right = mid - 1
    else:
      return mid
  return -1

