class Queue:
    def __init__(self):
        self.top = [] # The top stack
        self.bottom = [] # The bottom stack

    def is_empty(self):
        return None in (self.top, self.bottom)

    def enqueue(self, item):
        if not self.is_empty():
            raise RuntimeError("Queue is full")
        self.top.append(item)

    def dequeue(self):
        if self.is_empty():
            raise RuntimeError("Queue is empty")
        item = self.top.pop()
        item = self.bottom.pop()
        return item

    def peek(self):
        if self.is_empty():
            raise RuntimeError("Queue is empty")
        return self.top[-1]
