def longest_common_subsequence(a, b):
	n = len(a)
	m = len(b)

	dp = [[0] * (m+1) for _ in range(n+1)]

	for i in range(n+1):
		for j in range(m+1):
			if i == 0 or j == 0:
				dp[i][j] = 0
			elif a[i-1] == b[j-1]:
				dp[i][j] = dp[i-1][j-1] + 1
			else:
				dp[i][j] = max(dp[i-1][j], dp[i][j-1], dp[i-1][j-1])

	return dp[n][m]

