def BinarySearch(arr, item):
    if len(arr) == 0:
        return False
    beg = 0
    end = len(arr) - 1
    while beg <= end:
        mid = int((beg + end) / 2)
        if arr[mid] == item:
            return mid
        elif arr[mid] > item:
            end = beg + 1
        else:
            beg = mid + 1
    return -1

