#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::ifstream inputFile("input.txt");

    if (!inputFile.is_open()) {
        return 0;
    }

    std::string word;
    int count = 0;

    while (getline(inputFile, word)) {
        if (word == "your_word") {
            count++;
        }
    }

    inputFile.close();
    std::cout << "Number of occurrences of your_word: " << count << std::endl;
    return 0;
}

