// Program to find the nth Fibonacci number using dynamic programming
int nthFibonacci(int n){

    // base case
    if (n == 0)
        return 0;
    if (n == 1 || n == 2)
        return 1; 

    // Dynamic programming
    int dp1 = 0;
    int dp2 = 1;

    for (int i = 2; i <= n; i++)
    {
        int dp3 = dp1 + dp2;
        dp1 = dp2;
        dp2 = dp3;
    }

    return dp2;

}

