class StackQueue: 
    def __init__(self): 
        self.stack1 = [] 
        self.stack2 = [] 

    def push(self, item): 
        self.stack1.append(item) 

    def pop(self): 

        if self.stack1: 
            while self.stack1: 
                self.stack2.append(self.stack1.pop()) 

            result = self.stack2.pop() 
            while self.stack2: 
                self.stack1.append(self.stack2.pop()) 

            return result 
        else: 
            while self.stack2: 
                self.stack1.append(self.stack2.pop())

            return self.stack1.pop() 

    def peek(self): 
        if self.stack1: 
            while self.stack1: 
                self.stack2.append(self.stack1.pop()) 

            result = self.stack2.pop() 
            while self.stack2: 
                self.stack1.append(self.stack2.pop()) 

            return result 
        else: 
            result = self.stack1.pop() 
            while self.stack2: 
                self.stack1.append(self.stack2.pop()) 

            return result

