# class for Queue implementation using two Stacks
class Queue: 
    def __init__(self): 
        self.in_stack = [] # Stack for elements to be added in the Queue
        self.out_stack = [] # Stack for elements to be removed from the Queue

    # Enqueue an element at the rear of the Queue
    def enqueue(self, item): 
        if len(self.in_stack) == 0: 
            self.in_stack.append(item) 
        else: 
            self.in_stack[-1].append(item) 

    # Dequeue an element from the front of the Queue
    def dequeue(self): 
        if not self.is_empty(): 
            top_element = self.out_stack.pop() 
            self.in_stack.pop().append(top_element) 
        return self.out_stack.pop()

