def binary_search(arr, key): 
    high = len(arr) - 1 # index of last element 
    low = 0              # index of first element 

    while high >= low: 
        mid = (high + low) // 2
        if arr[mid] == key: 
            return mid 

        if arr[mid] > key: 
            high = mid - 1

        else: 
            low = mid + 1

    return -1 # element not found 

