#include <iostream>
#include <fstream>
#include <string>

int main()
{
    std::ifstream file("text_file.txt");
    int count = 0;

    if (file.is_open())
    {
        std::string line;

        while (std::getline(file, line))
        {
            if (line.find("word")!= std::string::npos)
                count++;
        }

        file.close();
        std::cout << "Number of occurrences of word: " << count;
    }
    else
    {
        std::cout << "Unable to open file";
    }

    return 0;
}

