def binary_search(arr, x):
    Low = 0
    High = len(arr) - 1
    while Low <= High:
       mid = (Low + High) // 2
       if arr[mid] == x:
           return mid
       elif arr[mid] > x:
           High = mid - 1
       else:
           Low = mid + 1
    return -1  # Not found

