class Queue: 
    def init(self): 
        self.queue1 = []
        self.queue2 = []

    def push(self,item):
        self.queue1.append(item)
        self.queue2.append(item)

    def is_empty(self): 
        return len(self.queue1) == 0 and len(self.queue2) == 0

    def pop(self): 
        # If first stack is not empty */
        if not self.queue1 == []:
            item = self.queue1.pop()
            # Reverse second stack 
            self.queue2.append(item)
            # Pop the first element from the reverse second stack */
            return self.queue2.pop()

        else: 
            # Overflow, Pop from second stack */
            return self.queue2.pop()

